export const DELETE_MOVIE = "DELETE_MOVIE";
export const ADD_MOVIE = "ADD_MOVIE";

export const addMovie = (id) => {
  movie.id = Date.now()
  return ({ type: ADD_MOVIE, payload: id})
}

export const deleteMovie = (movie) => {
  return ({ type: DELETE_MOVIE, payload: movie });
}
